import { Component } from '@angular/core';

@Component({
  selector: 'app-cta',
  templateUrl: './cta.component.html',
  styleUrls: ['./cta.component.css']
})
export class CtaComponent {
  ctaTitle = 'No importa de donde vengas si no a donde queires llegar';
  ctaDesc = 'Collaboration is key! Lets join forces and combine our skills to tackle your next project with a powerful synergy that guarantees success.';

  scrollToContact() {
    const contactElement = document.getElementById('contact-section');
    if (contactElement) {
      contactElement.scrollIntoView({ behavior: 'smooth' });
    }
  }
}
<section class="cta-section">
  <div class="cta-container">
    <h2 class="cta-title">{{ ctaTitle }}</h2>
    <p class="cta-desc">{{ ctaDesc }}</p>
    <button class="cta-button" (click)="scrollToContact()">
      <span>Contact</span>
    </button>
  </div>
</section>
