import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

export interface ContactData {
  name: string;
  email: string;
  subject: string;
  message: string;
}

@Injectable({
  providedIn: 'root'
})
export class ContactService {
  private apiUrl = 'https://api.example.com/contact'; // Reemplaza con tu API

  constructor(private http: HttpClient) { }

  sendMessage(data: ContactData): Observable<any> {
    // Si no tienes API, puedes usar EmailJS o similar
    // Por ahora retornamos un mock
    return new Observable(subscriber => {
      console.log('Enviando mensaje:', data);
      setTimeout(() => {
        subscriber.next({ success: true, message: 'Mensaje enviado correctamente' });
        subscriber.complete();
      }, 1000);
    });
  }

  // Para usar con EmailJS, descomentar y configurar:
  // sendMessage(data: ContactData): Observable<any> {
  //   return this.http.post(this.apiUrl, data);
  // }
}
