import { Component } from '@angular/core';

@Component({
  selector: 'app-hero',
  templateUrl: './hero.component.html',
  styleUrls: ['./hero.component.css']
})
export class HeroComponent {
  heroLabel = 'Hola mi nombre es';
  heroTitle = 'Jaciel Isai Hernandez';
  heroDesc = 'Apasionado por la tecnologia, soy junior developer, me gusta desarrollar, crear e indagar acerca de nuevas tecnologias para la implementación de mis proyectos personales';
  heroImg = 'assets/images/hero-image.png';

  scrollToContact() {
    const contactElement = document.getElementById('contact-section');
    if (contactElement) {
      contactElement.scrollIntoView({ behavior: 'smooth' });
    }
  }
}
<section class="hero-section">
  <div class="hero-container">
    <div class="hero-left">
      <label class="hero-label">{{ heroLabel }}</label>
      <h1 class="hero-title">{{ heroTitle }}</h1>
      <p class="hero-desc">{{ heroDesc }}</p>
      <button class="cta-button" (click)="scrollToContact()">
        <span>Contacto</span>
      </button>
    </div>
    <div class="hero-right">
      <img [src]="'assets/images/yo1.jpeg'" alt="Profile" class="hero-image">
    </div>
  </div>
</section>
