import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

export interface ContactData {
  name: string;
  email: string;
  subject: string;
  message: string;
}

@Injectable({
  providedIn: 'root'
})
export class ContactService {
  // üì± CAMBIAR ESTE N√öMERO por tu n√∫mero de WhatsApp (c√≥digo pa√≠s + n√∫mero, sin +)
  // Ejemplo: '34612345678' para Espa√±a, '5491234567890' para Argentina
  private whatsappNumber = '34612345678'; 

  constructor(private http: HttpClient) { }

  /**
   * Env√≠a el mensaje a WhatsApp Web
   * Abre WhatsApp en el navegador con el mensaje pre-llenado
   */
  sendMessage(data: ContactData): Observable<any> {
    return new Observable(subscriber => {
      try {
        // Construir el mensaje formateado
        const mensaje = this.construirMensaje(data);
        
        // Codificar el mensaje para URL
        const mensajeEncodificado = encodeURIComponent(mensaje);
        
        // URL de WhatsApp Web con el mensaje
        const urlWhatsapp = `https://wa.me/${this.whatsappNumber}?text=${mensajeEncodificado}`;
        
        // Abrir en nueva pesta√±a
        window.open(urlWhatsapp, '_blank');
        
        // Simular respuesta exitosa
        setTimeout(() => {
          subscriber.next({ 
            success: true, 
            message: 'Se abri√≥ WhatsApp. Confirma el env√≠o del mensaje.' 
          });
          subscriber.complete();
        }, 500);
      } catch (error) {
        subscriber.error(error);
      }
    });
  }

  /**
   * Construir el mensaje formateado
   */
  private construirMensaje(data: ContactData): string {
    return `*Nuevo mensaje de contacto* üìß\n\n` +
           `*Nombre:* ${data.name}\n` +
           `*Email:* ${data.email}\n` +
           `*Asunto:* ${data.subject}\n\n` +
           `*Mensaje:*\n${data.message}`;
  }

  // =====================================================
  // PARA USAR WHATSAPP BUSINESS API (OPCI√ìN AVANZADA)
  // =====================================================
  // Descomenta esto si usas Twilio u otro servicio:
  
  // private apiUrl = 'https://api.example.com/contact';
  // 
  // sendMessage(data: ContactData): Observable<any> {
  //   return this.http.post(this.apiUrl, data);
  // }
}
