import { Component } from '@angular/core';

@Component({
  selector: 'app-about',
  templateUrl: './about.component.html',
  styleUrls: ['./about.component.css']
})
export class AboutComponent {
  aboutImg = 'assets/images/about-image.png';
  aboutPoints = [
    {
      title: 'Mi Pasión por la Tecnología',
      desc: 'Desde pequeño me ha apasionado la tecnología. Me encanta descubrir nuevas herramientas y lenguajes de programación.'
    },
    {
      title: 'Desarrollo Full Stack',
      desc: 'Tengo experiencia en Frontend con Angular, React y Vue, así como Backend con Node.js, Python y bases de datos.'
    },
    {
      title: 'Proyectos Personales',
      desc: 'Me encanta crear mis propios proyectos para aprender e indagar sobre nuevas tecnologías y metodologías.'
    }
  ];
}
<section class="about-section">
  <label class="about-label">▶ Sobre mi</label>
  <div class="about-container">
    <div class="about-img">
      <img [src]="'assets/images/yo2.jpeg'" alt="About Me" class="image">
    </div>
    <div class="about-content">
      <div *ngFor="let point of aboutPoints" class="about-point">
        <h3 class="point-title">{{ point.title }}</h3>
        <p class="point-desc">{{ point.desc }}</p>
      </div>
    </div>
  </div>
</section>
